import './App.css'
import ContestInformation from '../Components/ContestInformation.js'

const Main = () => {

    const [displayCount, setDisplayCount] = useState(10)

    // もっと見る   ok
    const onClickShowMore = () => {
        setDisplayCount(displayCount => displayCount + 10)
        console.log(displayCount, "more")
    }

    return (
        <div className='main'>
            <div className='title'>
                <h2>Information about the contests</h2>
            </div>

            <ContestInformation />

            {displayCount < displayList.length && (
                <button
                    className='show-more-button'
                    onClick={() => { onClickShowMore() }}
                >
                    More...
                </button>
            )}

            {displayCount >= displayList.length && (
                <p className='no-more'>No more</p>
            )}

        </div>
    )
}

export default Main
