import React, { useState } from 'react'

const Header = (props) => {
    const [type, setType] = useState('all'),
        [keyword, setKeyword] = useState(''),
        [sort, setSort] = useState('newer')

    // 種類
    const typeHandleChange = (e) => {
        setType(e.target.value)
        searchFilter(e.target.value, keyword)
        sortFilter(sort)
        console.log('type')
    }

    // 検索
    const keywordChange = (e) => {
        setKeyword(e.target.value)
        searchFilter(type, e.target.value)
        console.log('text')
    }

    const searchFilter = (filterType, filterWord) => {
        let filteredText = '',
            filteredId = '',
            regWord = new RegExp(filterWord),
            regType = new RegExp(`^(?=.*${filterType}).*$`)
        props.setDisplayList(props.contestsList.filter((contest) => {
            filteredText = new RegExp(`${contest.start}${contest.duration_second}${contest.title}${contest.url}${contest.rate_change}`)
            filteredId = new RegExp(`${contest.id}`)
            console.log(contest.title, 'text:', regWord.test(`${contest.start}${contest.duration_second}${contest.title}${contest.url}${contest.rate_change}`), 'id:', regType.test(`${contest.id}`))
            return regWord.test(`${contest.start}${contest.duration_second}${contest.title}${contest.url}${contest.rate_change}`) && regType.test(`${contest.id}`)
        }))
        console.log(filterType, filterWord)
    }

    // 表示件数  ok
    const displayCountHandleChange = (e) => {
        props.setDisplayCount(+e.target.value)
        console.log(props.displayCount, "change")
    }

    // 並び替え   ok
    const sortHandleChange = (e) => {
        setSort(e.target.value)
        sortFilter(e.target.value)
        console.log(e.target.value)
    }

    const sortFilter = (e) => {
        if (e === 'newer') {
            props.setDisplayList(props.displayList.sort((a, b) => {
                if (a.start_epoch_second > b.start_epoch_second) {
                    return -1;
                } else {
                    return 1;
                }
            }))
        }
        if (e === 'older') {
            props.setDisplayList(props.displayList.sort((a, b) => {
                if (a.start_epoch_second < b.start_epoch_second) {
                    return -1;
                } else {
                    return 1;
                }
            }))
        }
        if (e === 'abcSort') {
            props.setDisplayList(props.displayList.sort((a, b) => {
                if (a.title < b.title) {
                    return -1;
                } else {
                    return 1;
                }
            }))
        }
    }

    return (
        <div className='header'>
            <h1 className='header-logo'>Atcoder Contests Information</h1>

            <div className="formats">
                <div className='format select'>
                    <select onChange={typeHandleChange} value={type}>
                        <option value=''>ALL</option>
                        <option value='abc'>ABC</option>
                        <option value='arc'>ARC</option>
                        <option value='agc'>AGC</option>
                        <option value='past'>PAST</option>
                        <option value='joi'>JOI</option>
                        <option value='jag'>JAG</option>
                    </select>
                </div>
                <div className='format select'>
                    <select onChange={displayCountHandleChange} value={props.displayCount}>
                        <option value='10'>10件表示</option>
                        <option value='50'>50件表示</option>
                        <option value='100'>100件表示</option>
                        <option value={props.displayList.length} >全件表示</option>
                    </select>
                </div>
                <div className='format select'>
                    <select onChange={sortHandleChange} value={sort} className="sortSelect">
                        <option value='newer'>新しい順</option>
                        <option value='older'>古い順</option>
                        <option value='abcSort'>五十音順</option>
                    </select>
                </div>
                <div className='format'>
                    <label class="format-focus">
                        <input
                            className='text-format'
                            type='text'
                            value={keyword}
                            placeholder='Keyword'
                            onChange={(e) => { keywordChange(e) }}
                        />
                    </label>
                    <span class="focus_line"></span>
                </div>
            </div>
        </div>
    )
}

export default Header
